INSERT INTO `jobs` VALUES (2,'Private Client Lawyer','TALENTED PRIVATE CLIENT LAWYER \r\n\r\n','<p>We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.</p>\r\n<p>We are seeking a high calibre Private Client lawyer committed to developing the Private Client business.&nbsp;</p>\r\n<p><br />This challenging &lsquo;hands on&rsquo; role deals with a full caseload of Wills, Probate, Trust and Tax Planning that also involves heading up the Company&rsquo;s Private Client team in Sheffield.</p>\r\n<p><br />The successful candidate should be commercially aware, have strong interpersonal skills with the ability to motivate a team.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Salary is negotiable.</p>\r\n<p>Please email all CVs detailing experience and salary to</p>\r\n<p><a href=\"mailto:cjs@bannerjones.co.uk\">cjs@bannerjones.co.uk</a>&nbsp;and <a href=\"mailto:lsg@bannerjones.co.uk\">lsg@bannerjones.co.uk</a></p>\r\n<p><br />Address:&nbsp; 24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</p>\r\n<p><br />Closing date is&nbsp;Friday 16th September&nbsp;2011</p>\r\n<p>We are an equal opportunities employer</p>','2011-09-16',2,2,0,NULL,1,0,'2011-07-04 09:09:31','2011-08-22 15:03:58',NULL,NULL,NULL,NULL,''),(3,'Talented Criminal Advocates','We have two opportunities in our Criminal Department for high calibre and committed Criminal Advocates.','<p>We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.</p>\r\n<p>We have two opportunities in our Criminal Department for high calibre and committed Criminal Advocates based in Chesterfield and Sheffield.</p>\r\n<h2>1.&nbsp;Barrister/Higher Courts Advocate</h2>\r\n<p>The successful applicant must be capable of handling a busy Crown Court caseload.&nbsp; Duty Solicitor qualification is preferred, if applicant is a Higher Courts Advocate.&nbsp;&nbsp;</p>\r\n<h2>2.&nbsp;Magistrates Courts Advocate</h2>\r\n<p>The successful applicant must be capable of handling a full caseload of both publically funded and private work.&nbsp; Duty Solicitor qualification is essential.</p>\r\n<p><br />The Company operates an out of hour&rsquo;s police station rota which, Solicitor applicants will be required to participate in.</p>\r\n<p>Salary is negotiable, according to experience and qualification.</p>\r\n<p>Please email all CVs detailing experience and current salary to <a href=\"mailto:lsg@bannerjones.co.uk\">lsg@bannerjones.co.uk</a>&nbsp;</p>\r\n<p>Address:&nbsp; 24 Glumangate, Chesterfield, S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</p>\r\n<p>Closing date is the 15th &nbsp;October 2011</p>\r\n<p>We are an equal opportunities employer</p>\r\n<p>&nbsp;</p>','2011-11-04',2,2,0,NULL,1,0,'2011-08-15 08:30:32','2011-10-13 12:47:19',NULL,NULL,NULL,NULL,''),(4,'Talented Criminal Advocate - Chesterfield','We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.\r\n\r\nWe have an opportunity in our Criminal Department for a high calibre and committed Criminal Advocate.','<p><strong>&nbsp;</strong><strong><span style=\"text-decoration: underline;\">Barrister/Higher Courts Advocate</span></strong></p>\r\n<p>The successful applicant must be capable of handling a busy Crown Court caseload.&nbsp; Duty Solicitor qualification is preferred, if applicant is a Higher Courts Advocate.&nbsp;&nbsp;&nbsp;</p>\r\n<p>The Company operates an out of hour&rsquo;s&nbsp;police station rota which, Solicitor applicants will be required to participate in.</p>\r\n<p>Salary is negotiable, according to experience and qualification.</p>\r\n<p>&nbsp;</p>\r\n<p>Please email all CVs detailing experience and&nbsp;current salary to <a href=\"mailto:lsg@bannerjones.co.uk\">lsg@bannerjones.co.uk</a> &nbsp;</p>\r\n<p>Address:&nbsp; 24 Glumangate, Chesterfield, S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</p>\r\n<p><em>We are an equal opportunities employer</em></p>','2012-10-31',2,2,0,NULL,1,0,'2011-12-14 16:01:10','2012-09-19 12:38:52',NULL,NULL,NULL,NULL,''),(5,'Administration Assistant ','Banner Jones Solicitors are looking for an Administration Assistant to be based within our Sheffield office.\r\n\r\nAre you a hardworking, enthusiastic and reliable person who is keen to work in a busy and well respected Law Company?\r\n','<p style=\"text-align: justify;\">You will be expected to carry out a wide range of duties which will include collating of documentation, photocopying and scanning, faxing, filing and other administration tasks.&nbsp; You will also have good communication skills, the ability to prioritise your work whilst also working as part of a team and on own initiative.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<br />Full time - Permanent. <br />Hours of business are Monday to Thursday 9.00 am to 5.15 pm <br />and Friday 9.00 am to 5.00 pm.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">If you are interested, please apply in writing to:-</p>\r\n<p>&nbsp;</p>\r\n<p>Lisa Gill</p>\r\n<p>Human Resources Manager</p>\r\n<p>Banner Jones Ltd</p>\r\n<p>24 Glumangate</p>\r\n<p>Chesterfield</p>\r\n<p>S40 1UA&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Or alternatively email: <a href=\"mailto:lsg@bannerjones.co.uk\">lsg@bannerjones.co.uk</a></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2012-02-08',2,2,0,NULL,1,0,'2012-01-27 10:17:23','2012-01-27 10:19:14',NULL,NULL,NULL,NULL,''),(6,'TALENTED PRIVATE CLIENT LAWYER – Based in Sheffield ','We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.\r\n','<p><strong>We are seeking a high calibre Private Client&nbsp;lawyer committed to developing the firm&rsquo;s Private Client department. &nbsp; </strong></p>\r\n<p><strong>This challenging &lsquo;hands on&rsquo; role deals with a full caseload of&nbsp;Wills, Probate, Trust and Tax Planning&nbsp;and it also involves heading up the firm&rsquo;s Private Client team in Sheffield. &nbsp;</strong></p>\r\n<p><strong>The successful candidate should be commercially aware, have strong interpersonal skills with the ability to motivate a team.&nbsp; A following would be preferable.&nbsp; </strong></p>\r\n<p><strong>Salary is negotiable. </strong></p>\r\n<p><strong>For more information on the position, please see our website: </strong><strong><a title=\"http://www.bannerjones.co.uk/personal/vacancies\" href=\"../personal/vacancies\">www.bannerjones.co.uk/personal/vacancies</a></strong></p>\r\n<p><strong>Please email all CVs detailing experience and salary to&nbsp;</strong><strong><a href=\"mailto:lsg@bannerjones.co.uk\">lsg@bannerjones.co.uk</a> </strong><strong>&nbsp;</strong><strong>&nbsp;</strong><strong></strong></p>\r\n<p><strong>Address:&nbsp;&nbsp;24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246&nbsp;560560</strong></p>\r\n<p><strong>Closing date is 3.12.2012</strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2012-10-07',2,2,0,NULL,1,0,'2012-08-07 12:49:42','2012-08-07 12:50:46',NULL,NULL,NULL,NULL,'');
