INSERT INTO `jobs` (`id`, `name`, `summary`, `main_content`, `closing_date`, `created_by`, `updated_by`, `recycled`, `recycled_at`, `display`, `position`, `created_at`, `updated_at`, `application_pack_file_name`, `application_pack_content_type`, `application_pack_file_size`, `application_pack_updated_at`, `application_pack_alt`) VALUES
(2, 'Private Client Lawyer', 'TALENTED PRIVATE CLIENT LAWYER \r\n\r\n', '<p>We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.</p>\r\n<p>We are seeking a high calibre Private Client lawyer committed to developing the Private Client business.&nbsp;</p>\r\n<p><br />This challenging &lsquo;hands on&rsquo; role deals with a full caseload of Wills, Probate, Trust and Tax Planning that also involves heading up the Company&rsquo;s Private Client team in Sheffield.</p>\r\n<p><br />The successful candidate should be commercially aware, have strong interpersonal skills with the ability to motivate a team.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Salary is negotiable.</p>\r\n<p>Please email all CVs detailing experience and salary to</p>\r\n<p><a href="mailto:cjs@bannerjones.co.uk">cjs@bannerjones.co.uk</a>&nbsp;and <a href="mailto:lsg@bannerjones.co.uk">lsg@bannerjones.co.uk</a></p>\r\n<p><br />Address:&nbsp; 24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</p>\r\n<p><br />Closing date is&nbsp;Friday 1st March 2013</p>\r\n<p>We are an equal opportunities employer</p>', '2013-02-18', 2, 2, 0, NULL, 1, 0, '2011-07-04 09:09:31', '2013-02-19 09:33:59', NULL, NULL, NULL, NULL, ''),
(5, 'Administration Assistant ', 'Administration Assistant based within our Sheffield office on a Permanent Full time basis.', '<p style="text-align: justify;">You will be expected to carry out a wide range of duties which will include collating of documentation, photocopying and scanning, faxing, filing and other administration tasks.&nbsp; You will also have good communication skills, the ability to prioritise your work whilst also working as part of a team and on own initiative.</p>\r\n<p style="text-align: justify;">&nbsp;<br />Full time - Permanent. <br />Hours of business are Monday to&nbsp;Friday 9am-5pm&nbsp;</p>\r\n<p>Please apply in writing with CVs and a supporting statement outlining your suitability and/or experience to:-</p>\r\n<p><strong>Lisa Gill, Human Resources Manager,&nbsp;</strong><strong>24 Glumangate, Chesterfield S40 1UA.&nbsp; </strong></p>\r\n<p><strong>or alternatively email : <a href="mailto:lsg@bannerjones.co.uk"><strong>lsg@bannerjones.co.uk</strong></a> &nbsp;&nbsp;</strong><strong>&nbsp;</strong></p>\r\n<p><strong></strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>', '2015-03-17', 2, 2, 0, NULL, 1, 0, '2012-01-27 10:17:23', '2015-03-03 11:19:59', NULL, NULL, NULL, NULL, ''),
(6, 'TALENTED PRIVATE CLIENT LAWYER – Based in Sheffield ', 'We are an energetic, forward thinking full service Law Firm, working from offices across North Derbyshire and South Yorkshire offering an exceptional quality service to our Clients.\r\n', '<p><strong>We are seeking a high calibre Private Client&nbsp;lawyer committed to developing the firm&rsquo;s Private Client department. &nbsp; </strong></p>\r\n<p><strong>This challenging &lsquo;hands on&rsquo; role deals with a full caseload of&nbsp;Wills, Probate, Trust and Tax Planning&nbsp;and it also involves heading up the firm&rsquo;s Private Client team in Sheffield. &nbsp;</strong></p>\r\n<p><strong>The successful candidate should be commercially aware, have strong interpersonal skills with the ability to motivate a team.&nbsp; A following would be preferable.&nbsp; </strong></p>\r\n<p><strong>Salary is negotiable. </strong></p>\r\n<p><strong>For more information on the position, please see our website: </strong><strong><a title="http://www.bannerjones.co.uk/personal/vacancies" href="../personal/vacancies">www.bannerjones.co.uk/personal/vacancies</a></strong></p>\r\n<p><strong>Please email all CVs detailing experience and salary to&nbsp;</strong><strong><a href="mailto:lsg@bannerjones.co.uk">lsg@bannerjones.co.uk</a> </strong><strong>&nbsp;</strong><strong>&nbsp;</strong><strong></strong></p>\r\n<p><strong>Address:&nbsp;&nbsp;24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246&nbsp;560560</strong></p>\r\n<p><strong>Closing date is 3.12.2012</strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '2012-10-07', 2, 2, 0, NULL, 1, 0, '2012-08-07 12:49:42', '2012-08-07 12:50:46', NULL, NULL, NULL, NULL, ''),
(7, 'Costs Lawyer – Chesterfield ', 'An exciting opportunity has arisen for an experienced Accredited Costs Lawyer to join an established Costs Department. \r\n\r\n \r\n\r\n', '<p>The ideal candidate will be required to handle their own caseload with autonomy but with the support from the rest of the team.&nbsp; They will have at least 3 + years publicly funded and private costs Litigation experience to include preparing and drafting claims for costs, preparing points of reply/dispute and sending off relevant documentation to the LSC for assessment, Legal aid costing experience in Family and Criminal is essential.</p>\r\n<p>Some advocacy experience would be advantageous.&nbsp;</p>\r\n<p>Salary is negotiable.&nbsp;</p>\r\n<p><strong>Please email all CVs detailing experience and salary to </strong><strong><a href="mailto:lsg@bannerjones.co.uk"><strong>lsg@bannerjones.co.uk</strong></a> &nbsp;&nbsp;</strong><strong></strong></p>\r\n<p><strong>Address:&nbsp;&nbsp;24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246&nbsp;560560</strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>', '2013-01-24', 2, 2, 0, NULL, 1, 0, '2012-10-17 09:47:06', '2012-11-01 10:57:07', NULL, NULL, NULL, NULL, ''),
(8, 'Administration Assistant ', 'Administration Assistant based within our Mansfield office on a Full time and Permanent basis.\r\nHours are Monday to Friday 9.00 am to 5.00 pm', '<p><br />You will be expected to carry out a wide range of duties which will include collating of documentation, photocopying and scanning, faxing, filing and other administration tasks. You will also have good communication skills, the ability to prioritise your work whilst also working as part of a team and on own initiative.</p>\r\n<p>Please apply in writing with CVs and a supporting statement outlining your suitability and/or experience to:-</p>\r\n<p>Grant Hodgkiss<br />Banner Jones Ltd<br />24 Glumangate<br />Chesterfield<br />S40 1UA</p>\r\n<p>Or alternatively by email to:- <a href="mailto:gnh@bannerjones.co.uk">gnh@bannerjones.co.uk</a></p>\r\n<p>&nbsp;</p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>\r\n<hr />', '2015-05-14', 2, 2, 0, NULL, 1, 0, '2012-11-01 10:39:15', '2015-04-30 13:10:09', NULL, NULL, NULL, NULL, ''),
(9, 'Team Leader/Supervisor ', 'An exciting opportunity has arisen for a Team Leader/Supervisor to join our company specialising in document storage, archiving, scanning and retrieval of data.   ', '<p>This position is based in Chesterfield and the hours of business are Monday to Thursday 5.30 pm to 9.30 pm.</p>\r\n<p>You will be expected to ensure daily and weekly targets are met and supervise a team whilst ensuring the maintenance of health and safety standards, together with the implementation of existing company policies and procedures.&nbsp;</p>\r\n<p>Salary is negotiable.&nbsp;&nbsp;</p>\r\n<p><strong>For more information on the position or if you wish to apply, please email your enquir orl CV detailing experience and current salary to </strong><strong><a href="mailto:lsg@bannerjones.co.uk"><strong>lsg@bannerjones.co.uk</strong></a> &nbsp;&nbsp;</strong><strong>&nbsp;</strong></p>\r\n<p><strong>Address:&nbsp;&nbsp;24 Glumangate, Chesterfield S40 1UA.&nbsp; Tel:&nbsp; 01246&nbsp;560560</strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>', '2012-11-23', 2, 2, 0, NULL, 1, 0, '2012-11-06 12:13:41', '2012-11-14 11:24:16', NULL, NULL, NULL, NULL, ''),
(10, 'SUPERVISOR DOCUMENT STORAGE OPERATIVE ', 'An exciting opportunity has arisen for a Supervisor Document Storage Operative specialising in document storage, archiving, scanning and retrieval of data.   ', '<p>This position is based in Chesterfield and the hours of business are Monday to Thursday 5.30 pm to 9.30 pm.&nbsp; &nbsp;&nbsp;</p>\r\n<p>You will be expected to ensure daily and weekly targets are met, supervise and motivate a team whilst ensuring the maintenance of health and safety standards together with existing company policies and procedures are carried out.&nbsp;</p>\r\n<p>Salary is negotiable, according to experience.&nbsp;&nbsp;</p>\r\n<p>Please email all CVs detailing experience and&nbsp;current salary to <a href="mailto:lsg@bannerjones.co.uk">lsg@bannerjones.co.uk</a>&nbsp;</p>\r\n<p>Address:&nbsp; 24 Glumangate, Chesterfield, S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</p>\r\n<p><em>We are an equal opportunities employer</em></p>', '2013-04-05', 2, 2, 0, NULL, 1, 0, '2013-03-27 15:08:13', '2013-03-27 15:09:06', NULL, NULL, NULL, NULL, ''),
(11, 'Administration Assistant', 'Administration Assistant based within our Chesterfield office on a Permanent Full time basis. \r\n\r\n', '<h2>Full Description</h2>\r\n<p style="text-align: justify;">You will be expected to carry out a wide range of duties which will include collating of documentation, photocopying and scanning, faxing, filing and other administration tasks.&nbsp; You will also have good communication skills, the ability to prioritise your work whilst also working as part of a team and on own initiative.</p>\r\n<p style="text-align: justify;">&nbsp;<br />Full time - Permanent. <br />Hours of business are Monday to&nbsp;Friday 9am-5pm&nbsp;</p>\r\n<p><strong>If you are interested please apply in writing to:</strong></p>\r\n<p><strong>Lisa Gill, Human Resources Manager,&nbsp;</strong><strong>24 Glumangate, Chesterfield S40 1UA.&nbsp; </strong></p>\r\n<p><strong>or alternatively email : <a href="mailto:lsg@bannerjones.co.uk"><strong>lsg@bannerjones.co.uk</strong></a> &nbsp;&nbsp;</strong><strong>&nbsp;</strong></p>\r\n<p><strong>Tel:&nbsp; 01246&nbsp;560560</strong></p>\r\n<p><strong><em>We are an equal opportunities employer</em></strong></p>', '2014-03-14', 2, NULL, 0, NULL, 1, 0, '2014-02-27 12:53:15', '2014-02-27 12:53:15', NULL, NULL, NULL, NULL, ''),
(12, 'Residential Conveyancing', 'We are currently seeking candidates who have experience in handling and dealing with a variety of residential conveyancing transactions in our offices based in Sheffield and Chesterfield.  We are also seeking a candidate for our Bilton Hammond office in Mansfield.  ', '<p><strong>2+ years experience that includes supporting a well established residential property team with the ability to work on their own with minimum supervision.&nbsp; Applicants will be expected to have good communication and organisational skills with a thorough working knowledge of conveyance practice and procedures.&nbsp; </strong></p>\r\n<p><strong>Competitive salary commensurate with qualifications and experience will be offered.&nbsp; </strong></p>\r\n<p>P<strong>lease apply in writing with a CV to Lisa Gill, Human Resources Manager, Banner Jones Ltd, 24 Glumangate, Chesterfield, S40 1UA, or alternatively by email to <a href="mailto:lsg@bannerjones.co.uk">lsg@bannerjones.co.uk</a> </strong></p>\r\n<p><strong>We have been accredited with the CQS mark and are an equal opportunities employer. </strong></p>\r\n&nbsp;<img src="../assets/stored_images/original/182_Accredited CQ_logo rgb.jpg?1364483474" border="0" alt="" hspace="0" width="214" height="95" />', '2014-07-13', 2, 2, 0, NULL, 1, 0, '2014-04-24 08:34:57', '2014-07-14 08:56:54', NULL, NULL, NULL, NULL, ''),
(13, 'Administration Assistant ', 'Banner Jones Ltd is looking for an Administration Assistant to be based within our office in Mansfield.', '<p>Full time - Permanent.Hours of business are Monday to Friday 9.00 am to 5.00 pm.&nbsp;</p>\r\n<p>You will be expected to carry out a wide range of duties which will include collating of documentation, photocopying and scanning, faxing, filing and other administration tasks. &nbsp;You will also have good communication skills, the ability to prioritise your work whilst also working as part of a team and on own initiative.&nbsp;</p>\r\n<p><span style="color: #000000; font-family: ''Times New Roman''; font-size: small;">If you are interested, please apply in writing to:-</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: small; font-family: Times New Roman; color: #000000;">Lisa Gill<br /></span><span style="color: #000000; font-family: ''Times New Roman''; font-size: small;">Human Resources Manager<br /></span><span style="color: #000000; font-family: ''Times New Roman''; font-size: small;">Banner Jones Ltd<br /></span><span style="color: #000000; font-family: ''Times New Roman''; font-size: small;">24 Glumangate<br /></span><span style="font-family: ''Times New Roman''; font-size: small; color: #000000;">Chesterfield<br /></span><span style="font-family: ''Times New Roman''; font-size: small; color: #000000;">S40 1UA&nbsp;</span></p>\r\n<p><span style="font-size: small; font-family: Times New Roman; color: #000000;">Or alternatively email: </span><span style="font-size: small; font-family: Times New Roman; color: #0000ff;"><a href="mailto:lsg@bannerjones.co.uk">lsg@bannerjones.co.uk</a></span></p>\r\n<p>&nbsp;</p>', '2015-01-16', 2, 2, 0, NULL, 1, 0, '2014-07-18 08:38:30', '2015-01-07 16:44:12', NULL, NULL, NULL, NULL, ''),
(14, 'Criminal Defence Lawyer', 'An opportunity has arisen in our Criminal Defence Department for a high calibre and committed Criminal Advocate. The successful applicant will be based in our Chesterfield office, but will be required to cover our other offices in Sheffield and Mansfield, as necessary.', '<p><span style="font-size: small;"><span style="color: #000000;">The ideal candidate will be required to work 3 days per week and must be capable of handling a full caseload of both publically funded and private work.&nbsp; They will have at least 2 + years PQE and Duty Solicitor qualification is essential. </span></span></p>\r\n<p><span style="font-size: small; color: #000000;">&nbsp;</span></p>\r\n<p><span style="font-size: small;"><span style="color: #000000;">The Company operates an out of hour&rsquo;s&nbsp;police station rota, which, the successful applicant will be required to participate in. </span></span></p>\r\n<p><span style="font-size: small; color: #000000;">&nbsp;</span></p>\r\n<p><span style="font-size: small;"><span style="color: #000000;">Salary is negotiable, according to experience and qualification</span></span></p>\r\n<p><span style="font-size: small; color: #000000;">&nbsp;</span></p>\r\n<p><span style="font-size: small; color: #000000;">Please email all CVs detailing experience and&nbsp;current salary to </span><a href="mailto:lsg@bannerjones.co.uk"><span style="font-size: small; color: #0000ff;">lsg@bannerjones.co.uk</span></a><span style="font-size: small;"><span style="color: #000000;">&nbsp; </span></span></p>\r\n<p><span style="font-size: small;"><span style="color: #000000;">Address:&nbsp; 24 Glumangate, Chesterfield, S40 1UA.&nbsp; Tel:&nbsp; 01246 560560</span></span></p>\r\n<p><span style="font-size: small;"><span style="color: #000000;">Closing date is&nbsp;15<sup>th</sup> August 2014</span></span></p>\r\n<p><span style="font-size: small; color: #000000;">&nbsp;</span></p>\r\n<p><em><span style="font-size: small;"><span style="color: #000000;">We are an equal opportunities employer</span></span></em></p>\r\n<p><span style="font-size: small; font-family: Times New Roman; color: #000000;">&nbsp;</span></p>', '2014-08-15', 2, 2, 0, NULL, 1, 0, '2014-07-28 08:40:51', '2014-07-28 10:27:01', NULL, NULL, NULL, NULL, ''),
(15, 'Marketing Assistant', 'Banner Jones Ltd is looking for a Marketing Assistant based within our Chesterfield office.   \r\nFull or Part time hours (negotiable) - Permanent.  \r\nHours of business are Monday to Friday 9.00 am to 5.00 pm. \r\n\r\nWe are seeking a highly motivated enthusiastic Marketing Assistant to provide administrative support to our Marketing Manager.  The role involves working across several brands striving to raise company image and maximising profile.   \r\n', '<p class="MsoNormal" style="text-align: justify; margin: 0cm 0cm 0pt;"><span style="font-size: small;"><span style="color: #000000;"><span style="font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;">The successful candidate will be expected to assist with the development and implementation of Marketing activities, have good communication and IT skills, the ability to prioritise work, whilst working as part of a team and on own initiative. </span><span style="font-size: 10pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #222222;">&nbsp;</span></span></span></p>\r\n<p class="MsoNormal" style="text-align: justify; margin: 0cm 0cm 0pt; mso-layout-grid-align: none;"><span style="font-size: 10pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #222222;">&nbsp;</span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt;"><span style="font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;"><span style="font-size: small;"><span style="color: #000000;">This &lsquo;hands on&rsquo; role will include a wide range of duties as follows: </span></span></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt;"><span style="font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;"><span style="font-size: small; color: #000000;">&nbsp;</span></span></p>\r\n<p class="MsoListParagraphCxSpFirst" style="margin: 0cm 0cm 0pt 38.15pt; text-indent: -18pt; mso-add-space: auto; mso-list: l0 level1 lfo1;"><span style="font-family: Symbol; mso-fareast-font-family: Symbol; ms